mapper.insert:
insert into sys_acl (id, code, name, 
  acl_module_id, url, type, 
  status, seq, remark, 
  operator, operate_time, operate_ip
  )
values (#{id,jdbcType=INTEGER}, #{code,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
  #{aclModuleId,jdbcType=INTEGER}, #{url,jdbcType=VARCHAR}, #{type,jdbcType=INTEGER}, 
  #{status,jdbcType=INTEGER}, #{seq,jdbcType=INTEGER}, #{remark,jdbcType=VARCHAR}, 
  #{operator,jdbcType=VARCHAR}, #{operateTime,jdbcType=TIMESTAMP}, #{operateIp,jdbcType=VARCHAR}
  )

mapper.select:
select count(*) from sys_acl


        and id = #{id,jdbcType=INTEGER}


        and code = #{code,jdbcType=VARCHAR}


        and name = #{name,jdbcType=VARCHAR}


        and acl_module_id = #{aclModuleId,jdbcType=INTEGER}


        and url = #{url,jdbcType=VARCHAR}


        and type = #{type,jdbcType=INTEGER}


        and status = #{status,jdbcType=INTEGER}


        and seq = #{seq,jdbcType=INTEGER}


        and remark = #{remark,jdbcType=VARCHAR}


        and operator = #{operator,jdbcType=VARCHAR}


        and operate_time = #{operateTime,jdbcType=TIMESTAMP}


        and operate_ip = #{operateIp,jdbcType=VARCHAR}

select

from sys_acl where 1 = 1

    and id = #{param1.id,jdbcType=INTEGER}


    and code = #{param1.code,jdbcType=VARCHAR}


    and name = #{param1.name,jdbcType=VARCHAR}


    and acl_module_id = #{param1.aclModuleId,jdbcType=INTEGER}


    and url = #{param1.url,jdbcType=VARCHAR}


    and type = #{param1.type,jdbcType=INTEGER}


    and status = #{param1.status,jdbcType=INTEGER}


    and seq = #{param1.seq,jdbcType=INTEGER}


    and remark = #{param1.remark,jdbcType=VARCHAR}


    and operator = #{param1.operator,jdbcType=VARCHAR}


    and operate_time = #{param1.operateTime,jdbcType=TIMESTAMP}


    and operate_ip = #{param1.operateIp,jdbcType=VARCHAR}


    order by

        ${order.property} ${order.direction}



    limit ${param2.offset}, ${param2.pageSize}

SELECT * from sys_acl where 1=1
and id in
#{id}
